using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Windows.Input;namespace NOTAM.ViewModel{    /// <summary>    /// Represents an actionable item displayed by a View.    /// </summary>    public class CommandViewModel : ViewModelBase    {        public CommandViewModel(string displayName, ICommand command)        {            if (command == null)                throw new ArgumentNullException("command");            base.DisplayName = displayName;            this.Command = command;        }        public ICommand Command { get; private set; }    }    public class CommandGroupViewModel : ViewModelBase    {        public CommandGroupViewModel(string GroupName, IList<CommandViewModel> commands)        {            if (commands == null)                throw new ArgumentNullException("commands");            base.DisplayName = GroupName;            this.Commands = commands;        }        public IList<CommandViewModel> Commands { get; private set; }    }}